import React, { Component } from "react";

class Info extends Component {
/*
  constructor(props) {
    super(props);
    this.state = {
      firstName: ""
    };
  }
  inputChange = (e) => {
    const firstName = e.target.value;
    this.setState(() => ({ firstName }));
  };
  displayNameHandler = (e) => {
    e.preventDefault();
    this.setState(prevState => ({ FirstName: prevState.firstName }));
  };
*/
state= {
    name: null,
    birthMonth: null,
    about: null
}

handleChange= (e) => {
  this.setState({
    [e.target.id]: e.target.value
  }
  )
}
handleSubmit= (e) => {
  e.preventDefault();
  this.props.addInfo(this.state);
}
  render() {
    return (
      <div className='center'>
        <h4 className='container center'>Person Information</h4>
        <p> This is Person Information page.</p>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor='name' > Name </label>
          <input type='text' id='name' onChange={this.handleChange} /> <br />
          <label htmlFor='birthMonth'> Birth Month </label>
          <input type='text' id='birthMonth' onChange={this.handleChange}/> <br />
          <label htmlFor='about'> About </label>
          <input type='text' id='about' onChange={this.handleChange} /> <br />
          <button> Submit </button>
        </form>
        <Info displayInfos={this.state.displayInfos} />
        <AddInfo addInfo={this.addInfo} />
      </div>
    );
  }
}
export default Info;
