import React, { Component } from 'react';
//import './AddInfo.css';
class AddInfo extends Component {
    state= {
      name: null,
      birthMonth: null,
      about: null
  }

  handleChange= (e) => {
    this.setState({
      [e.target.id]: e.target.value
    }
    )
  }

  handleSubmit= (e) => {
    e.preventDefault();
    console.log('Inside AddInfo Component', this.state);
    this.props.addInfo(this.state);
  }
  render(){
    return(
      <div className='AddInfo'>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor='name' > Name </label>
          <input type='text' id='name' onChange={this.handleChange} /> <br />
          <label htmlFor='birthMonth'> Birth Month </label>
          <input type='text' id='birthMonth' onChange={this.handleChange}/> <br />
          <label htmlFor='age'> About </label>
          <input type='textarea' height="10" column="10" id='intro' onChange={this.handleChange} /> <br />
          <button> Submit </button>
        </form>
      </div>
    )
  }
}

export default AddInfo;
