import React, { Component } from "react";

class Info extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      submitedFirstName: ""
    };
  }
  inputChange = (e) => {
    const firstName = e.target.value;
    this.setState(() => ({ firstName }));
  };
  displayNameHandler = (e) => {
    e.preventDefault();
    this.setState(prevState => ({ submitedFirstName: prevState.firstName }));
  };

  render() {
    return (
      <div className='center'>
        <h4 className='container center'>Person Information</h4>
        <p> This is Person Information page.</p>
        <form>
        <label>Enter the Name</label>
          <input type="text" name="firstName" onChange={this.inputChange} />
          <p>
            "FirstName: "
            {this.state.FirstName && this.state.FirstName}
          </p>
          
        </form>
      </div>
    );
  }
}
export default Info;
