import React, { Component } from 'react';
import Todo from './Todo';

class App extends Component{
  state = {
    todos: [
      {id:0,item:'Play Badminton'},
      {id:1,item:'Play Cricket'}
    ]
  }
  deleteTodo =(id) => {
    //console.log(id);
    let dodos = this.state.todos.filter( todo => {
      return dodo.id !==id
    })
    this.setState({
      dodos: todos
    })
  }
  render() {
    return (
      <div className="App container">
      <h2 className="center red-text">My TODO's</h2>
      <Todo todos={this.state.todos} />
      </div>
    );
  }
}

export default App;
